<?php

namespace Repair\StoreBundle\Entity;

use Doctrine\ORM\EntityRepository;

/**
 * adminRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class adminRepository extends EntityRepository
{
public function authadmin($name,$password)
{
 	$em = $this->getEntityManager();
 	$query = $em->createQuery("SELECT a.id,a.username FROM RepairStoreBundle:admin a where a.username='".	$name."' and a.password='".$password."'")->getResult();
  		return $query;
 
}
public function display()
{
	$em = $this->getEntityManager();
	$result=$em->createQuery("SELECT u FROM RepairStoreBundle:users u");
        return $result->getArrayResult();
}
public function affdetailes($id)
{
 	$em = $this->getEntityManager();
 	$query = $em->createQuery("SELECT a FROM RepairStoreBundle:affliates a WHERE a.parentid=$id")->getArrayResult();
 	return $query;
}
}
